    49→    return callApi('PATCH', `/apps/${APP_IDS.DOZENTEN}/records/${id}`, { fields });
    50→  }
    51→  static async deleteDozentenEntry(id: string) {
    52→    return callApi('DELETE', `/apps/${APP_IDS.DOZENTEN}/records/${id}`);
    53→  }
