@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700&display=swap');

@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --font-sans: 'Plus Jakarta Sans', sans-serif;
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-destructive-foreground: var(--destructive-foreground);
  --color-success: var(--success);
  --color-success-foreground: var(--success-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

:root {
  --font-sans: 'Plus Jakarta Sans', sans-serif;
  --radius: 0.5rem;

  /* Core palette - Academic Navy & Gold */
  --background: hsl(220 20% 97%);
  --foreground: hsl(222 47% 11%);

  --card: hsl(0 0% 100%);
  --card-foreground: hsl(222 47% 11%);

  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(222 47% 11%);

  --primary: hsl(222 47% 25%);
  --primary-foreground: hsl(0 0% 98%);

  --secondary: hsl(220 14% 96%);
  --secondary-foreground: hsl(222 47% 20%);

  --muted: hsl(220 14% 90%);
  --muted-foreground: hsl(220 9% 46%);

  --accent: hsl(43 74% 49%);
  --accent-foreground: hsl(222 47% 15%);

  --destructive: hsl(0 84% 60%);
  --destructive-foreground: hsl(0 0% 98%);

  --success: hsl(152 69% 31%);
  --success-foreground: hsl(0 0% 98%);

  --border: hsl(220 13% 91%);
  --input: hsl(220 13% 91%);
  --ring: hsl(222 47% 25%);

  /* Charts */
  --chart-1: hsl(222 47% 35%);
  --chart-2: hsl(43 74% 49%);
  --chart-3: hsl(152 69% 31%);
  --chart-4: hsl(200 70% 45%);
  --chart-5: hsl(280 60% 50%);

  /* Sidebar */
  --sidebar: hsl(222 47% 20%);
  --sidebar-foreground: hsl(0 0% 98%);
  --sidebar-primary: hsl(43 74% 49%);
  --sidebar-primary-foreground: hsl(222 47% 15%);
  --sidebar-accent: hsl(222 47% 30%);
  --sidebar-accent-foreground: hsl(0 0% 98%);
  --sidebar-border: hsl(222 47% 30%);
  --sidebar-ring: hsl(43 74% 49%);

  /* Custom shadows */
  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-card: 0 1px 3px 0 rgb(0 0 0 / 0.08), 0 1px 2px -1px rgb(0 0 0 / 0.06);
  --shadow-elevated: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);

  /* Gradients */
  --gradient-primary: linear-gradient(135deg, hsl(222 47% 25%), hsl(222 47% 35%));
  --gradient-accent: linear-gradient(135deg, hsl(43 74% 49%), hsl(43 74% 59%));
  --gradient-subtle: linear-gradient(180deg, hsl(220 20% 97%), hsl(220 20% 95%));
}

.dark {
  --background: hsl(222 47% 11%);
  --foreground: hsl(0 0% 98%);
  --card: hsl(222 47% 15%);
  --card-foreground: hsl(0 0% 98%);
  --popover: hsl(222 47% 15%);
  --popover-foreground: hsl(0 0% 98%);
  --primary: hsl(43 74% 49%);
  --primary-foreground: hsl(222 47% 11%);
  --secondary: hsl(222 47% 20%);
  --secondary-foreground: hsl(0 0% 98%);
  --muted: hsl(222 47% 20%);
  --muted-foreground: hsl(220 14% 60%);
  --accent: hsl(43 74% 49%);
  --accent-foreground: hsl(222 47% 11%);
  --destructive: hsl(0 70% 55%);
  --destructive-foreground: hsl(0 0% 98%);
  --success: hsl(152 69% 40%);
  --success-foreground: hsl(0 0% 98%);
  --border: hsl(222 47% 25%);
  --input: hsl(222 47% 25%);
  --ring: hsl(43 74% 49%);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-family: var(--font-sans);
  }
}

@layer components {
  /* Hero Stats Card */
  .stat-card {
    @apply bg-card rounded-lg p-6 shadow-[var(--shadow-card)] transition-all duration-200;
  }

  .stat-card:hover {
    @apply shadow-[var(--shadow-elevated)];
    transform: translateY(-2px);
  }

  .stat-card-hero {
    @apply bg-primary text-primary-foreground rounded-lg p-6 shadow-[var(--shadow-elevated)];
    background: var(--gradient-primary);
  }

  /* Enrollment Badge */
  .enrollment-badge {
    @apply inline-flex items-center gap-2 px-3 py-1.5 rounded-full text-sm font-medium;
  }

  .enrollment-badge-normal {
    @apply bg-muted text-muted-foreground;
  }

  .enrollment-badge-full {
    @apply bg-accent text-accent-foreground;
  }

  /* Data Table Styles */
  .data-table {
    @apply w-full;
  }

  .data-table th {
    @apply text-xs font-semibold uppercase tracking-wider text-muted-foreground;
  }

  /* Tab Navigation */
  .tab-nav {
    @apply inline-flex items-center gap-1 bg-muted/50 p-1 rounded-lg;
  }

  .tab-item {
    @apply px-4 py-2 text-sm font-medium rounded-md transition-all duration-200;
  }

  .tab-item-active {
    @apply bg-card text-foreground shadow-sm;
  }

  .tab-item-inactive {
    @apply text-muted-foreground hover:text-foreground hover:bg-card/50;
  }

  /* Form Field Styles */
  .form-label {
    @apply text-sm font-medium text-foreground;
  }

  .form-input {
    @apply w-full px-3 py-2 bg-card border border-input rounded-md text-sm;
    @apply focus:outline-none focus:ring-2 focus:ring-ring focus:border-transparent;
    @apply placeholder:text-muted-foreground;
  }

  /* Action Buttons */
  .btn-action {
    @apply inline-flex items-center gap-1.5 px-3 py-1.5 text-sm font-medium rounded-md transition-all duration-200;
  }

  .btn-action-edit {
    @apply text-primary hover:bg-primary/10;
  }

  .btn-action-delete {
    @apply text-destructive hover:bg-destructive/10;
  }

  /* Status Badges */
  .status-badge {
    @apply inline-flex items-center gap-1 px-2 py-0.5 rounded-full text-xs font-medium;
  }

  .status-paid {
    @apply bg-success/10 text-success;
  }

  .status-unpaid {
    @apply bg-destructive/10 text-destructive;
  }

  /* Page Header */
  .page-header {
    @apply mb-8;
  }

  .page-title {
    @apply text-2xl font-bold tracking-tight text-foreground;
  }

  .page-subtitle {
    @apply text-muted-foreground mt-1;
  }
}

@layer utilities {
  /* Smooth transitions */
  .transition-smooth {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Glow effects */
  .glow-primary {
    box-shadow: 0 0 20px hsl(222 47% 25% / 0.3);
  }

  .glow-accent {
    box-shadow: 0 0 20px hsl(43 74% 49% / 0.4);
  }
}

