              <TabsTrigger value="dozenten">Dozenten</TabsTrigger>
              <TabsTrigger value="teilnehmer">Teilnehmer</TabsTrigger>
              <TabsTrigger value="raeume">Räume</TabsTrigger>
              <TabsTrigger value="anmeldungen">Anmeldungen</TabsTrigger>
            </TabsList>
            <Button
